import ConfigExtendCommand from './ConfigExtendCommand';
import * as names from './ConfigCommandNames';

export default {
    [names.DEFAULTS]: ConfigDefaultsCommand,
    [names.MERGE]: ConfigMergeCommand,
    [names.EXTEND]: ConfigExtendCommand,
};

import {
    Container,
    Transient
} from 'constitute';
import Config from './Config';
import ConfigCache from './ConfigCache';
import ConfigEnvironment from './ConfigEnvironment';
import ConfigPatternCache from './ConfigPatternCache';
import ConfigStringResolver from './ConfigStringResolver';
import ConfigPathResolver from './ConfigPathResolver';
import ConfigLoader from './ConfigLoader';
import ConfigFactory from './ConfigFactory';
import ConfigOptionsResolver from './ConfigOptionsResolver';
import ConfigDefaultsCommand from './ConfigDefaultsCommand';
import ConfigMergeCommand from './ConfigMergeCommand';
import ConfigExtendCommand from './ConfigExtendCommand';
import ConfigCommandFactory from './ConfigCommandFactory';

/**
 * @private
 * @type {WeakMap}
 */
const CONTAINER = new WeakMap();

/**
 * @class
 */
class ConfigContainer {
    /**
     * @constructor
     */
    constructor() {
        CONTAINER.set(this, new Container());

        this.setUp();
    }

    /**
     * @protected
     * @type {Container}
     */
    get container() {
        return CONTAINER.get(this);
    }

    /**
     * @protected
     * @returns {void}
     */
    setUp() {
        const container = this.container;

        container.bindValue(ConfigContainer, this);
        container.bindValue(ConfigEnvironment, new ConfigEnvironment(Object.entries(process.env)));
        container.bindClass(ConfigCache, ConfigCache, [
            ConfigEnvironment
        ]);
        container.bindValue(ConfigPatternCache, new ConfigPatternCache());
        container.bindClass(ConfigStringResolver, ConfigStringResolver, [
            ConfigEnvironment,
            ConfigPatternCache
        ]);
        container.bindClass(ConfigPathResolver, ConfigPathResolver, [
            ConfigStringResolver
        ]);
        container.bindClass(ConfigLoader, ConfigLoader, [
            ConfigPathResolver,
            ConfigCache,
            ConfigFactory
        ]);
        container.bindClass(ConfigFactory, ConfigFactory, [
            ConfigContainer
        ]);
        container.bindClass(Config, Config, Transient.with([
            ConfigCommandFactory
        ]));
        container.bindClass(ConfigOptionsResolver, ConfigOptionsResolver, [
            ConfigStringResolver
        ]);
        container.bindClass(ConfigDefaultsCommand, ConfigDefaultsCommand, [
            ConfigOptionsResolver
        ]);
        container.bindClass(ConfigMergeCommand, ConfigMergeCommand, [
            ConfigOptionsResolver
        ]);
        container.bindClass(ConfigExtendCommand, ConfigExtendCommand, [
            ConfigOptionsResolver,
            ConfigLoader,
            ConfigFactory
        ]);
        container.bindClass(ConfigCommandFactory, ConfigCommandFactory, [
            ConfigContainer
        ]);
    }

    /**
     * @param {*} T
     * @returns {*}
     */
    resolve(T) {
        return this.container.constitute(T);
    }

    /**
     * @param {*} T
     * @returns {Function}
     */
    proxy(T) {
        return () => this.resolve(T);
    }
}

export default ConfigContainer;

import {
    defaultsDeep
} from 'lodash';
import ConfigCommand from './ConfigCommand';

/**
 * @class
 * @extends {ConfigCommand}
 */
class ConfigDefaultsCommand extends ConfigCommand {
    /**
     * @override
     */
    execute(config, options) {
        const value = this.optionsResolver.resolve(config, options);

        defaultsDeep(config, value);
    }
}

export default ConfigDefaultsCommand;

/**
 * Returns config without any modifications
 * @param {Config} config
 * @returns {Config}
 */
export default config => config;

import RecursiveIterator from 'recursive-iterator';

/**
 * @external RecursiveIterator
 * @see https://github.com/nervgh/recursive-iterator
 */

/**
 * @class
 * @extends {external:RecursiveIterator}
 */
class ConfigDependencyIterator extends RecursiveIterator {
    /**
     * @constructor
     * @param {ConfigDependency} root
     * @param {Number} [bypassMode=0]
     * @param {Boolean} [ignoreCircular=true]
     * @param {Number} [maxDeep]
     */
    constructor(root, bypassMode = 0, ignoreCircular = true, maxDeep) {
        super(root, bypassMode, ignoreCircular, maxDeep);
    }

    /**
     * @override
     */
    getStatesOfChildNodes(node, path, deep) {
        return node.children.map(child => this.getState(node, child, child.root.filename, path.concat(child.root.filename), deep + 1));
    }
}

export default ConfigDependencyIterator;

import ConfigDependencyIterator from './ConfigDependencyIterator';

/**
 * @private
 * @type {WeakMap}
 */
const ROOT = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const CHILDREN = new WeakMap();

/**
 * Supports {@link https://developer.mozilla.org/en/docs/Web/JavaScript/Reference/Iteration_protocols|iteration protocols} via {@link ConfigDependencyIterator}
 * @class
 */
class ConfigDependency {
    /**
     * @constructor
     * @param {Config} root
     * @param {ConfigDependency[]} children
     */
    constructor(root, children = []) {
        ROOT.set(this, root);
        CHILDREN.set(this, children);
    }

    /**
     * @readonly
     * @type {Config}
     */
    get root() {
        return ROOT.get(this);
    }

    /**
     * @readonly
     * @type {ConfigDependency[]}
     */
    get children() {
        return CHILDREN.get(this);
    }

    /**
     * @returns {ConfigDependencyIterator}
     */
    [Symbol.iterator]() {
        return new ConfigDependencyIterator(this);
    }
}

export default ConfigDependency;

import {
    isFunction,
    isUndefined
} from 'lodash';

/**
 * @class
 * @extends {Map}
 */
class ConfigEnvironment extends Map {
    /**
     * @param {...Object<String,*>} values
     * @returns {ConfigEnvironment}
     */
    setAll(...values) {
        values.forEach(obj => {
            for (const [key, value] of Object.entries(obj)) {
                this.set(key, value);
            }
        });

        return this;
    }

    /**
     * @param {*} key
     * @returns {*}
     */
    valueOf(key) {
        const value = this.get(key);

        return isFunction(value) ? value.call(this, this) : value;
    }

    /**
     * @param {*} key
     * @param {*} [defaultValue]
     * @returns {*}
     */
    getOrDefault(key, defaultValue) {
        const value = this.valueOf(key);

        return isUndefined(value) ? defaultValue : value;
    }
}

export default ConfigEnvironment;

import {
    isObject,
    isString
} from 'lodash';
import Config from './Config';
import ConfigCommand from './ConfigCommand';
import DEFAULT_TRANSFORM from './ConfigDefaultTransform';
import CLEANUP_TRANSFORM from './ConfigCleanupTransform';

/**
 * @typedef {Object<String,ConfigTransform[]>} ConfigExtendOptions
 */

/**
 * @typedef {String|Object<String,ConfigTransform>|ConfigExtendOptions} ConfigExtendPossibleOptions
 */

/**
 * @private
 * @type {WeakMap}
 */
const LOADER = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const FACTORY = new WeakMap();

/**
 * @class
 * @extends {ConfigCommand}
 */
class ConfigExtendCommand extends ConfigCommand {
    /**
     * @constructor
     * @param {ConfigOptionsResolver} optionsResolver
     * @param {ConfigLoader} loader
     * @param {ConfigFactory} factory
     */
    constructor(optionsResolver, loader, factory) {
        super(optionsResolver);

        LOADER.set(this, loader);
        FACTORY.set(this, factory);
    }

    /**
     * @readonly
     * @type {ConfigLoader}
     */
    get loader() {
        return LOADER.get(this);
    }

    /**
     * @readonly
     * @type {ConfigFactory}
     */
    get factory() {
        return FACTORY.get(this);
    }

    /**
     * @override
     */
    execute(config, options) {
        const normalizedOptions = ConfigExtendCommand.normalizeOptions(options);

        normalizedOptions.forEach(value => {
            const { filename, transforms } = this.optionsResolver.resolve(config, value);
            const pendingConfig = this.loader.loadConfig(filename);

            if (pendingConfig instanceof Config) {
                config.dependencyTree.children.push(pendingConfig.dependencyTree);

                let prevConfig = pendingConfig.clone();

                transforms.forEach(transform => {
                    const currConfig = transform.call(config, prevConfig);

                    if (!isObject(currConfig)) {
                        prevConfig = {};
                    } else {
                        prevConfig = currConfig;
                    }

                    if (!(prevConfig instanceof Config)) {
                        prevConfig = this.factory.createConfig({}).merge(prevConfig);
                    }
                });

                if (prevConfig instanceof Config) {
                    config.merge(prevConfig.toObject());
                }
            }
        });
    }

    /**
     * @param {ConfigExtendPossibleOptions} options
     * @returns {ConfigExtendOptions[]}
     */
    static normalizeOptions(options) {
        let normalizedOptions = [];

        if (isString(options)) {
            normalizedOptions = [{
                filename: options,
                transforms: [
                    DEFAULT_TRANSFORM,
                    CLEANUP_TRANSFORM
                ]
            }];
        } else if (isObject(options)) {
            for (const [filename, transform] of Object.entries(options)) {
                const transforms = Array.isArray(transform) ? transform : [transform];

                normalizedOptions.push({
                    filename,
                    transforms: [
                        ...transforms,
                        CLEANUP_TRANSFORM
                    ]
                });
            }
        }

        return normalizedOptions;
    }
}

export default ConfigExtendCommand;

import {
    isFunction,
    isObject
} from 'lodash';
import Config from './Config';
import ConfigList from './ConfigList';
import { env } from './ConfigArgv';

/**
 * @private
 * @type {WeakMap}
 */
const CONTAINER = new WeakMap();

/**
 * @class
 */
class ConfigFactory {
    /**
     * @constructor
     * @param {ConfigContainer} container
     */
    constructor(container) {
        CONTAINER.set(this, container);
    }

    /**
     * @readonly
     * @type {ConfigContainer}
     */
    get container() {
        return CONTAINER.get(this);
    }

    /**
     * @private
     * @param {Object|Config} value
     * @returns {Config}
     */
    initWith(value) {
        let config;

        if (value instanceof Config) {
            config = value.clone();
        } else {
            config = this.container.resolve(Config).merge(value);
        }

        return config;
    }

    /**
     * @param {Function|Object|Object[]} value
     * @returns {Config|ConfigList}
     */
    createConfig(value) {
        let config;

        if (isFunction(value)) {
            config = value(env);
        }

        if (Array.isArray(value)) {
            config = ConfigList.from(value, x => this.initWith(x));
        } else if (isObject(value)) {
            config = this.initWith(value);
        }

        return config;
    }
}

export default ConfigFactory;

import {
    set,
    unset,
    get,
    has
} from 'lodash';
import ConfigDependency from './ConfigDependency';
import ConfigCommandInvoker from './ConfigCommandInvoker';
import * as commandNames from './ConfigCommandNames';

/**
 * @private
 * @type {WeakMap}
 */
const DEPENDENCY_TREE = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const COMMAND_FACTORY = new WeakMap();

/**
 * @class
 */
class Config {
    /**
     * @constructor
     * @param {ConfigCommandFactory} commandFactory
     */
    constructor(commandFactory) {
        COMMAND_FACTORY.set(this, commandFactory);
    }

    /**
     * @readonly
     * @type {ConfigCommandFactory}
     */
    get commandFactory() {
        return COMMAND_FACTORY.get(this);
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.extend('./test/fixtures/webpack.1.config.js');
     *
     * for (const {node} of config.dependencyTree) {
     *   console.log(node.root.filename);
     * }
     * // ./test/fixtures/webpack.1.config.js
     * // ./test/fixtures/webpack.2.config.js
     * // ./test/fixtures/webpack.3.config.js
     * // ./test/fixtures/webpack.5.config.js
     * // ./test/fixtures/webpack.4.config.js
     * @description Holds information about [included]{@link Config#extend} configs
     * @readonly
     * @type {ConfigDependency}
     */
    get dependencyTree() {
        if (!DEPENDENCY_TREE.has(this)) {
            DEPENDENCY_TREE.set(this, new ConfigDependency(this));
        }

        return DEPENDENCY_TREE.get(this);
    }

    /**
     * @private
     * @param {ConfigDependency} value
     */
    set dependencyTree(value) {
        DEPENDENCY_TREE.set(this, value);
    }

    /**
     * import Config from 'webpack-config';
     *
     * export default new Config().defaults({
     *    debug: true
     * }, {
     *    profile: false
     * });
     * @example
     * import Config from 'webpack-config';
     *
     * export default new Config().defaults(() => {
     *     return {
     *         debug: true
     *     };
     * });
     * @description Adds `values` if they are missing
     * @param {...ConfigOptions} values
     * @returns {Config}
     */
    defaults(...values) {
        return ConfigCommandInvoker.invoke(commandNames.DEFAULTS, this, ...values);
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * export default new Config().merge({
     *    debug: true
     * }, {
     *    profile: false
     * });
     * @example
     * import Config from 'webpack-config';
     *
     * export default new Config().merge(() => {
     *     return {
     *         debug: true
     *     };
     * });
     * @description Merges `values`
     * @param {...ConfigOptions} values
     * @returns {Config}
     */
    merge(...values) {
        return ConfigCommandInvoker.invoke(commandNames.MERGE, this, ...values);
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * export default new Config().extend('./test/fixtures/webpack.1.config.js');
     * @example
     * import Config from 'webpack-config';
     *
     * // Loads from `node_modules/react-redux/webpack.config.js`
     * export default new Config().extend('react-redux/webpack.config.js');
     * @example
     * import Config from 'webpack-config';
     *
     * // Loads from `node_modules/webpack-config-my/webpack.config.js`
     * export default new Config().extend('my/webpack.config.js');
     * @example
     * import Config from 'webpack-config';
     *
     * export default new Config().extend({
     *    './test/fixtures/webpack.1.config.js': config => {
     *        delete config.tags;
     *
     *        return config;
     *    }
     * });
     * @example
     * import Config from 'webpack-config';
     *
     * export default new Config().extend({
     *    './test/fixtures/webpack.1.config.js': [config => {
     *        delete config.tags;
     *
     *        return config;
     *    }, config => {
     *        delete config.profile;
     *
     *        return config;
     *    }]
     * });
     * @description Helps to extend config using local file or shareable config file which should be hosted under `node_modules`
     * @param {...ConfigExtendPossibleOptions} values
     * @returns {Config}
     */
    extend(...values) {
        return ConfigCommandInvoker.invoke(commandNames.EXTEND, this, ...values);
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.merge({
     *   debug: true
     * });
     *
     * console.log(config.clone());
     * // Config { debug: true }
     * @description Creates copy of {@link Config}
     * @returns {Config}
     */
    clone() {
        const config = new Config(this.commandFactory);

        config.dependencyTree = new ConfigDependency(config, this.dependencyTree.children);

        return config.merge(this.toObject());
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.merge({
     *   debug: true
     * });
     *
     * console.log(config.toObject());
     * // Object { debug: true }
     * @description Returns plain `Object` representation of {@link Config}
     * @returns {Object}
     */
    toObject() {
        const properties = {};

        for (const [key, value] of Object.entries(this)) {
            if (this.has(key)) {
                properties[key] = value;
            }
        }

        return properties;
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.set('debug', true);
     *
     * console.log(config.toObject());
     * // Object { debug: true }
     * @description Sets `value` at `path`
     * @param {String} path
     * @param {*} value
     * @return {Config}
     */
    set(path, value) {
        set(this, path, value);

        return this;
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.set('debug', true);
     *
     * console.log(config.get('debug'));
     * // true
     * @description Gets `value` at `path`
     * @param {String} path
     * @return {*}
     */
    get(path) {
        return get(this, path);
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.set('debug', true).remove('debug');
     *
     * console.log(config.get('debug'));
     * // undefined
     * @description Removes `value` at `path`
     * @param {String} path
     * @return {Config}
     */
    remove(path) {
        unset(this, path);

        return this;
    }

    /**
     * @example
     * import Config from 'webpack-config';
     *
     * const config = new Config();
     *
     * config.set('debug', true);
     *
     * console.log(config.has('debug'));
     * // true
     * @description Checks if `value` exist at `path`
     * @param {String} path
     * @return {Boolean}
     */
    has(path) {
        return has(this, path);
    }

    /**
     * @returns {Object}
     */
    toJSON() {
        return this.toObject();
    }
}

export default Config;

/**
 * @class
 * @extends {Array}
 */
class ConfigList extends Array {}

export default ConfigList;

import {
    isString
} from 'lodash';

/**
 * @private
 * @type {WeakMap}
 */
const PATH_RESOLVER = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const CACHE = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const FACTORY = new WeakMap();

/**
 * @class
 */
class ConfigLoader {
    /**
     * @constructor
     * @param {ConfigPathResolver} pathResolver
     * @param {ConfigCache} cache
     * @param {ConfigFactory} factory
     */
    constructor(pathResolver, cache, factory) {
        PATH_RESOLVER.set(this, pathResolver);
        CACHE.set(this, cache);
        FACTORY.set(this, factory);
    }

    /**
     * @readonly
     * @type {ConfigPathResolver}
     */
    get pathResolver() {
        return PATH_RESOLVER.get(this);
    }

    /**
     * @readonly
     * @type {ConfigCache}
     */
    get cache() {
        return CACHE.get(this);
    }

    /**
     * @readonly
     * @type {ConfigFactory}
     */
    get factory() {
        return FACTORY.get(this);
    }

    /**
     * @param {String} filename
     * @returns {Config|ConfigList}
     */
    loadConfig(filename) {
        filename = this.pathResolver.resolve(filename);

        let config = this.cache.get(filename);

        if (config) {
            config = this.factory.createConfig(config);
        }

        if (config && !isString(config.filename)) {
            config.filename = filename;
        }

        return config;
    }
}

export default ConfigLoader;

import {
    mergeWith,
    isObject
} from 'lodash';
import ConfigCommand from './ConfigCommand';

/**
 * @class
 * @extends {ConfigCommand}
 */
class ConfigMergeCommand extends ConfigCommand {
    /**
     * @override
     */
    execute(config, options) {
        const value = this.optionsResolver.resolve(config, options);

        mergeWith(config, value, (x, y) => { // eslint-disable-line consistent-return
            if (Array.isArray(x) && Array.isArray(y)) {
                return [...x, ...y];
            } else if (Array.isArray(x) && isObject(y)) {
                return [...x, y];
            } else if (isObject(x) && Array.isArray(y)) {
                return [x, ...y];
            }
        });
    }
}

export default ConfigMergeCommand;

import {
    isFunction,
    isString
} from 'lodash';
import RecursiveIterator from 'recursive-iterator';

/**
 * @function
 * @name ConfigTransform
 * @param {Config} config
 * @returns {Config|Object}
 */

/**
 * @typedef {Object|ConfigTransform} ConfigOptions
 */

/**
 * @private
 * @type {WeakMap}
 */
const STRING_RESOLVER = new WeakMap();

/**
 * @class
 */
class ConfigOptionsResolver {
    /**
     * @constructor
     * @param {ConfigStringResolver} stringResolver
     */
    constructor(stringResolver) {
        STRING_RESOLVER.set(this, stringResolver);
    }

    /**
     * @readonly
     * @type {ConfigStringResolver}
     */
    get stringResolver() {
        return STRING_RESOLVER.get(this);
    }

    /**
     * @private
     * @param {Config} config
     * @param {ConfigOptions} options
     * @returns {Object}
     */
    static valueOf(config, options) {
        return isFunction(options) ? options.call(config, config) : options;
    }

    /**
     * @param {Config} config
     * @param {ConfigOptions} options
     * @returns {Object}
     */
    resolve(config, options) {
        const value = ConfigOptionsResolver.valueOf(config, options);

        for (const {parent, node, key} of new RecursiveIterator(value, 1, true)) {
            if (isString(node)) {
                parent[key] = this.stringResolver.resolve(node);
            }
        }

        return value;
    }
}

export default ConfigOptionsResolver;

import {
    isString
} from 'lodash';
import ConfigStrategyList from './ConfigStrategyList';
import DEFAULT_RESOLVERS from './ConfigPathResolvers';

/**
 * @private
 * @type {WeakMap}
 */
const PATH_RESOLVERS = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const STRING_RESOLVER = new WeakMap();

/**
 * @class
 */
class ConfigPathResolver {
    /**
     * @constructor
     * @param {ConfigStringResolver} stringResolver
     * @param {Function[]} [pathResolvers]
     */
    constructor(stringResolver, pathResolvers = DEFAULT_RESOLVERS) {
        STRING_RESOLVER.set(this, stringResolver);
        PATH_RESOLVERS.set(this, ConfigStrategyList.from(pathResolvers));
    }

    /**
     * @readonly
     * @type {ConfigStringResolver}
     */
    get stringResolver() {
        return STRING_RESOLVER.get(this);
    }

    /**
     * @readonly
     * @type {ConfigStrategyList}
     */
    get pathResolvers() {
        return PATH_RESOLVERS.get(this);
    }

    /**
     * @param {String} value
     * @returns {String}
     */
    resolve(value) {
        value = this.stringResolver.resolve(value);

        return this.pathResolvers.resolve(value, x => isString(x));
    }
}

export default ConfigPathResolver;

import {
    resolve
} from 'path';

/**
 * @private
 * @type {String}
 */
const MODULE_PREFIX = 'webpack-config';

export default [
    /**
     * @example `require('<module-name>')`
     * @param {String} value
     * @throws {Error}
     * @returns {String}
     */
    value => require.resolve(value),

    /**
     * @example `require('webpack-config-<name>')`
     * @param {String} value
     * @throws {Error}
     * @returns {String}
     */
    value => require.resolve(`${MODULE_PREFIX}-${value}`),

    /**
     * @example `path.resolve('<file-name>')`
     * @param {String} value
     * @returns {String}
     */
    value => resolve(value)
];

import {
    template
} from 'lodash';

/**
 * @private
 * @type {RegExp}
 */
const DEFAULT_INTERPOLATE = /\[([\w\s]+?)]/g;

/**
 * @private
 * @type {WeakMap}
 */
const INTERPOLATE = new WeakMap();

/**
 * @class
 * @extends {Map}
 */
class ConfigPatternCache extends Map {
    /**
     * @constructor
     * @param {RegExp} [interpolate=/\[([\w\s]+?)]/g]
     */
    constructor(interpolate = DEFAULT_INTERPOLATE) {
        super();

        this.interpolate = interpolate;
    }

    /**
     * @type {RegExp}
     */
    get interpolate() {
        return INTERPOLATE.get(this);
    }

    /**
     * @example
     * import {
     *   patternCache
     * } from 'webpack-config';
     *
     * patternCache.interpolate = /{([\w\s]+?)}/g;
     * @param {RegExp} value
     */
    set interpolate(value) {
        INTERPOLATE.set(this, value);
    }

    /**
     * @param {*} key
     * @returns {RegExp}
     */
    getOrSet(key) {
        if (!this.has(key)) {
            this.set(key, key);
        }

        return this.get(key);
    }

    /**
     * @override
     * @param {*} key
     * @param {String} value
     * @returns {RegExp}
     */
    set(key, value) {
        return super.set(key, this.compile(value));
    }

    /**
     * @param {String} value
     * @param {Object} options
     * @returns {String}
     */
    eval(value, options = {}) {
        const compiledTemplate = this.getOrSet(value);

        return compiledTemplate(options);
    }

    /**
     * @param {String} value
     * @returns {Function}
     */
    compile(value) {
        return template(value, {
            interpolate: this.interpolate
        });
    }
}

export default ConfigPatternCache;

import {
    isError
} from 'lodash';

/**
 * @class
 * @extends {Array}
 */
class ConfigStrategyList extends Array {
    /**
     * @param {*} value
     * @param {Function} predicate
     * @returns {*}
     */
    resolve(value, predicate) {
        for (const resolver of this) {
            try {
                const resolvedValue = resolver(value),
                    throwsError = isError(resolvedValue) || resolvedValue instanceof Error;

                if (predicate(resolvedValue) && !throwsError) {
                    value = resolvedValue;
                    break;
                }
            } catch (err) {} // eslint-disable-line no-empty
        }

        return value;
    }
}

export default ConfigStrategyList;

/**
* @private
* @type {WeakMap}
*/
const ENVIRONMENT = new WeakMap();

/**
 * @private
 * @type {WeakMap}
 */
const PATTERN_CACHE = new WeakMap();

/**
 * @class
 */
class ConfigStringResolver {
    /**
     * @constructor
     * @param {ConfigEnvironment} environment
     * @param {ConfigPatternCache} patternCache
     */
    constructor(environment, patternCache) {
        ENVIRONMENT.set(this, environment);
        PATTERN_CACHE.set(this, patternCache);
    }

    /**
     * @readonly
     * @type {ConfigEnvironment}
     */
    get environment() {
        return ENVIRONMENT.get(this);
    }

    /**
     * @readonly
     * @type {ConfigPatternCache}
     */
    get patternCache() {
        return PATTERN_CACHE.get(this);
    }

    /**
     * @param {String} value
     * @returns {String}
     */
    resolve(value) {
        const options = {};

        for (const key of this.environment.keys()) {
            options[key] = this.environment.valueOf(key);
        }

        let resolvedValue;

        try {
            resolvedValue = this.patternCache.eval(value, options);
        } catch (err) {
            resolvedValue = value;
        }

        return resolvedValue;
    }
}

export default ConfigStringResolver;

import Config from './Config';
import ConfigEnvironment from './ConfigEnvironment';
import ConfigCache from './ConfigCache';
import ConfigPatternCache from './ConfigPatternCache';
import ConfigContainer from './ConfigContainer';

/**
 * @private
 * @type {ConfigContainer}
 */
const container = new ConfigContainer();

/**
 * Proxy class which automatically fills {@link Config} constructor dependencies
 * @class
 * @extends {Config}
 */
const ConfigProxy = container.proxy(Config);

/**
 * @module webpack-config
 */

export default ConfigProxy;

export {
    /**
     * @type {ConfigProxy}
     */
    ConfigProxy as Config
};

/**
 * @type {ConfigEnvironment}
 */
export const environment = container.resolve(ConfigEnvironment);

/**
 * @type {ConfigCache}
 */
export const cache = container.resolve(ConfigCache);

/**
 * @type {ConfigPatternCache}
 */
export const patternCache = container.resolve(ConfigPatternCache);

/**
 * Returns `webpack.config.js`
 * @type {String}
 */
export const FILENAME = 'webpack.config.js';
